import classes from './AvailableMeals.module.css';
import Card from '../UI/Card';
import Mealitem from './MealItem';
import { useEffect, useState } from 'react';


const AvailableMeals=() =>{
const [meals,setMeals]= useState([]);

  useEffect(()=>{
    const fetchMeals= async ()=>{
      const response=await fetch('https://console.firebase.google.com/u/0/project/food-order-28ada/database/food-order-28ada-default-rtdb/data/~2F');
      const responseData=await response.json();
      
      const loadedMeals=[];

      for(const key in responseData){
        loadedMeals.push({
          id:key,
          name:responseData[key].name,
          description:responseData[key].description,
          price:responseData[key].price,
        });
      }
      setMeals(loadedMeals);

    };
  fetchMeals();
    },[]);
    
  
            const mealsList=meals.map(meal=><Mealitem id={meal.id} key={meal.id} name={meal.name} description={meal.description} price={meal.price}/>);
    return (
        <section className={classes.meals}>
           <Card><ul>
                {mealsList}
            </ul>
            </Card> 
        </section>
    );

};
export default AvailableMeals;
